	public void InitializeCustomCode()
	{
		// ** Wizard Insert Location - Do not delete 'Begin/End Wizard Added Variable Initialization' lines **
		// Begin Wizard Added Variable Initialization

		this._LoadIDSalesOrder = string.Empty;

		// End Wizard Added Variable Initialization

		// Begin Wizard Added Custom Method Calls

		InitializeSalesOrderAdapter();
		// End Wizard Added Custom Method Calls

		_custShipAdapter = new CustShipAdapter(this.oTrans);
		_custShipAdapter.BOConnect();

		_partAdapter = new PartAdapter(this.oTrans);
		_partAdapter.BOConnect();

		_workstationAdapter = new WorkstationAdapter(this.oTrans);
		_workstationAdapter.BOConnect();

		_warehseAdapter = new WarehseAdapter(this.oTrans);
		_warehseAdapter.BOConnect();

		_ud11Adapter = new UD11Adapter(this.oTrans);
		_ud11Adapter.BOConnect();

		this._edvShipHead = new EpiDataView();
		this._edvShipHead.dataView = new DataView(this._custShipAdapter.CustShipData.ShipHead);
		if ((this.oTrans.EpiDataViews.ContainsKey("ShipHeadView") == false))
		{
			this.oTrans.Add("ShipHeadView", this._edvShipHead);
		}

		this._edvShipDtl = new EpiDataView();
		this._edvShipDtl.dataView = new DataView(this._custShipAdapter.CustShipData.ShipDtl);
		if ((this.oTrans.EpiDataViews.ContainsKey("ShipDtlView") == false))
		{
			this.oTrans.Add("ShipDtlView", this._edvShipDtl);
		}

		this._edvShipDtl.dataView.Table.ColumnChanging += new DataColumnChangeEventHandler(this.ShipDtl_BeforeFieldChange);

		txtLoadID.BeforeExitEditMode += LoadIDChanged;
		txtRel.AfterExitEditMode += RelScanned;
		txtPartNum.Leave += PartScanned;
		txtBinNum.Leave += BinScanned;
		cmdNewRelease.Click += NewRelease;
		cmdCloseShipment.Click += CloseShipment;

		UltraGridColumn column = grdQuantity.DisplayLayout.Bands[0].Columns.Insert(grdQuantity.DisplayLayout.Bands[0].Columns.Count,"Remain Qty");
		column.DataType = typeof(int);
		column.CellActivation = Activation.ActivateOnly; 
		column = grdQuantity.DisplayLayout.Bands[0].Columns.Insert(grdQuantity.DisplayLayout.Bands[0].Columns.Count,"Pack Qty");
		column.DataType = typeof(int);
		column.CellActivation = Activation.ActivateOnly; 
		grdQuantity.DisplayLayout.AutoFitStyle = AutoFitStyle.ResizeAllColumns;
		HHConfirmShipForm.Activated += FormFocus;
	}

	public void FormFocus(object sender, EventArgs args)
	{
		txtLoadID.Focus();
	}

	public void DestroyCustomCode()
	{
		// ** Wizard Insert Location - Do not delete 'Begin/End Wizard Added Object Disposal' lines **
		// Begin Wizard Added Object Disposal

		this._edvShipDtl.dataView.Table.ColumnChanging -= new DataColumnChangeEventHandler(this.ShipDtl_BeforeFieldChange);
		
		if ((this._salesOrderAdapter != null))
		{
			this._salesOrderAdapter.Dispose();
			this._salesOrderAdapter = null;
		}

		if ((this._custShipAdapter != null))
		{
			if(this._edvShipHead.dataView.Count > 0 && !(bool)this._edvShipHead.dataView[0]["ReadyToInvoice"])
			{
				this._custShipAdapter.Delete(this._edvShipHead.dataView[0].Row);
			}
			this._custShipAdapter.Dispose();
			this._custShipAdapter = null;
		}

		if ((this._partAdapter != null))
		{
			this._partAdapter.Dispose();
			this._partAdapter = null;
		}

		if ((this._workstationAdapter != null))
		{
			this._workstationAdapter.Dispose();
			this._workstationAdapter = null;
		}

		if ((this._warehseAdapter != null))
		{
			this._warehseAdapter.Dispose();
			this._warehseAdapter = null;
		}

		if ((this._ud11Adapter != null))
		{
			this._ud11Adapter.Dispose();
			this._ud11Adapter = null;
		}

		this._edvSalesOrder = null;
		this._edvShipHead = null;
		this._edvShipDtl = null;
		this._LoadIDSalesOrder = null;
		// End Wizard Added Object Disposal

		// Begin Custom Code Disposal

		// End Custom Code Disposal
		txtLoadID.BeforeExitEditMode -= LoadIDChanged;
		txtRel.AfterExitEditMode -= RelScanned;
		txtPartNum.Leave -= PartScanned;
		txtBinNum.Leave -= BinScanned;
		cmdNewRelease.Click -= NewRelease;
		cmdCloseShipment.Click -= CloseShipment;
	}

	private void InitializeSalesOrderAdapter()
	{
		// Create an instance of the Adapter.
		this._salesOrderAdapter = new SalesOrderAdapter(this.oTrans);
		this._salesOrderAdapter.BOConnect();

		// Add Adapter Table to List of Views
		// This allows you to bind controls to the custom UD Table
		this._edvSalesOrder = new EpiDataView();
		this._edvSalesOrder.dataView = new DataView(this._salesOrderAdapter.SalesOrderData.OrderRel);
		if ((this.oTrans.EpiDataViews.ContainsKey("SalesOrderView") == false))
		{
			this.oTrans.Add("SalesOrderView", this._edvSalesOrder);
		}

		if ((this.oTrans.PrimaryAdapter != null))
		{
			// this.oTrans.PrimaryAdapter.GetCurrentDataSet(Ice.Lib.Searches.DataSetMode.RowsDataSet).Tables.Add(this._edvSalesOrder.dataView.Table.Clone())
		}

	}

	private void GetSalesOrderData(string loadID)
	{
		if (this._LoadIDSalesOrder != loadID)
		{			
			string ud11WhereClause = "Key1 = \'" + loadID + "\'";
			string workstationWhereClause = "WorkStationID = \'" + (oTrans.Session as Session).WorkstationID + "\'";
			System.Collections.Hashtable workstationWhereClauses = new System.Collections.Hashtable(1);
			workstationWhereClauses.Add("WorkStation", workstationWhereClause);

			SearchOptions workstationSearchOptions = SearchOptions.CreateRuntimeSearch(workstationWhereClauses, DataSetMode.RowsDataSet);
			this._workstationAdapter.InvokeSearch(workstationSearchOptions);

			if(this._workstationAdapter.WorkstationData.WorkStation.Rows.Count > 0)
			{
				string warehseWhereClause = "WarehouseCode = \'" + this._workstationAdapter.WorkstationData.WorkStation.Rows[0]["WarehouseCode"] + "\'";
				System.Collections.Hashtable warehseWhereClauses = new System.Collections.Hashtable(1);
				warehseWhereClauses.Add("Warehse", warehseWhereClause);

				SearchOptions warehseSearchOptions = SearchOptions.CreateRuntimeSearch(warehseWhereClauses, DataSetMode.RowsDataSet);
				this._warehseAdapter.InvokeSearch(warehseSearchOptions);

				if(this._warehseAdapter.WarehseData.Warehse.Rows.Count > 0)
				{
					ud11WhereClause += " AND " + "Key2 = \'" + this._warehseAdapter.WarehseData.Warehse.Rows[0]["Plant"] + "\'";
				}
			}

			System.Collections.Hashtable ud11WhereClauses = new System.Collections.Hashtable(1);
			ud11WhereClauses.Add("UD11", ud11WhereClause);

			SearchOptions ud11SearchOptions = SearchOptions.CreateRuntimeSearch(ud11WhereClauses, DataSetMode.RowsDataSet);
			this._ud11Adapter.InvokeSearch(ud11SearchOptions);
		
			string whereClause = "";

			foreach(DataRow row in this._ud11Adapter.UD11Data.UD11.Rows)
			{
				whereClause += "(OrderNum = \'" + row["Key3"] + "\' AND OrderLine = \'" + row["Key4"] + "\' AND OrderRelNum = \'" + row["Key5"] + "\') OR ";
			}

			if(whereClause == "")
			{
				return;
			}

			whereClause = whereClause.Remove(whereClause.Length - 4);
			
			System.Collections.Hashtable whereClauses = new System.Collections.Hashtable(1);
			whereClauses.Add("OrderRel", whereClause);

			// Call the adapter search.
			SearchOptions searchOptions = SearchOptions.CreateRuntimeSearch(whereClauses, DataSetMode.RowsDataSet);
			this._salesOrderAdapter.InvokeSearch(searchOptions);

			if ((this._salesOrderAdapter.SalesOrderData.OrderRel.Rows.Count > 0))
			{
				this._edvSalesOrder.Row = 0;
			} else
			{
				this._edvSalesOrder.Row = -1;
			}
			
			int i = 0;
			foreach (UltraGridRow row in grdQuantity.Rows)
			{
				row.Cells["Remain Qty"].Value = (decimal)this._salesOrderAdapter.SalesOrderData.OrderRel.Rows[i]["SellingReqQty"] - (decimal)this._salesOrderAdapter.SalesOrderData.OrderRel.Rows[i]["TotalJobStockShipped"];
				row.Cells["Pack Qty"].Value = this._salesOrderAdapter.SalesOrderData.OrderRel.Rows[i]["TotalJobStockShipped"];
				i++;
			}

			// Notify that data was updated.
			this._edvSalesOrder.Notify(new EpiNotifyArgs(this.oTrans, this._edvSalesOrder.Row, this._edvSalesOrder.Column));

			// Set key fields to their new values.
			this._LoadIDSalesOrder = loadID;
			if(this._edvShipHead.dataView.Count > 0 && !(bool)this._edvShipHead.dataView[0]["ReadyToInvoice"])
			{
				this._custShipAdapter.Delete(this._edvShipHead.dataView[0].Row);
			}
			if(_custShipAdapter.GetNewShipHead())
			{
				this._edvShipHead.Notify(new EpiNotifyArgs(this.oTrans, this._edvShipHead.Row, this._edvShipHead.Column));
				_custShipAdapter.Update();
				NewLine();
			}
		}
	}

	private void LoadIDChanged(object sender, EventArgs args)
	{
		GetSalesOrderData(txtLoadID.Text);
	}

	private void RelScanned(object sender, EventArgs args)
	{
		foreach(DataRow row in this._salesOrderAdapter.SalesOrderData.OrderRel.Rows)
		{
			if(row["OrderNum"].ToString() == _edvShipDtl.CurrentDataRow["OrderNum"].ToString() && 
			   row["OrderLine"].ToString() == _edvShipDtl.CurrentDataRow["OrderLine"].ToString() && 
			   row["OrderRelNum"].ToString() == txtRel.Text.ToString())
			{
				foreach(DataRow headRow in this._salesOrderAdapter.SalesOrderData.OrderHed.Rows)
				{
					if(headRow["OrderNum"].ToString() == row["OrderNum"].ToString())
					{
						_edvShipDtl.CurrentDataRow["CustNum"] = headRow["CustNum"];
						_edvShipHead.dataView[0]["RateGrpCode"] = headRow["RateGrpCode"];
						break;
					}
				}
				string whereClause = "WorkStationID = \'" + (oTrans.Session as Session).WorkstationID + "\'";
				System.Collections.Hashtable whereClauses = new System.Collections.Hashtable(1);
				whereClauses.Add("WorkStation", whereClause);

				SearchOptions searchOptions = SearchOptions.CreateRuntimeSearch(whereClauses, DataSetMode.RowsDataSet);
				this._workstationAdapter.InvokeSearch(searchOptions);
				if(this._workstationAdapter.WorkstationData.WorkStation.Rows.Count > 0)
				{
					_edvShipDtl.CurrentDataRow["WarehouseCode"] = this._workstationAdapter.WorkstationData.WorkStation.Rows[0]["WarehouseCode"];
				}
				else
				{
					_edvShipDtl.CurrentDataRow["WarehouseCode"] = row["WarehouseCode"];
				}
				expectedPart = row["PartNum"].ToString();
				_edvShipDtl.CurrentDataRow["PartNum"] = row["PartNum"];
				_edvShipDtl.CurrentDataRow["RevisionNum"] = row["RevisionNum"];
				_edvShipDtl.CurrentDataRow["LineDesc"] = ((PartDataSet.PartDataTable)((PartImpl)_partAdapter.BusinessObject).GetByID(row["PartNum"].ToString()).Tables[0])[0].PartDescription;
				_edvShipDtl.CurrentDataRow["InventoryShipUOM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["IUM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["SalesUM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["JobShipUOM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["OurRemainUM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["OurReqUM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["OurShippedUM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["SellingRemainUM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["SellingReqUM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["SellingShipmentUM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["SellingShippedUM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["PartNumSalesUM"] = row["SalesUM"];
				_edvShipDtl.CurrentDataRow["PartNumIUM"] = row["SalesUM"];
				_edvShipHead.dataView[0]["CurrencyCode"] = row["CurrencyCode"];
				break;
			}
		}
	}

	private void PartScanned(object sender, EventArgs args)
	{
		if(txtPartNum.Text == "")
		{
			txtPartNum.Focus();
			MessageBox.Show("Part blank please scan part", "", MessageBoxButtons.OK, MessageBoxIcon.Error);
		}
		else if(expectedPart != null && txtPartNum.Text != expectedPart)
		{
			txtPartNum.Focus();
			MessageBox.Show("Wrong part please scan correct part: " + expectedPart, "", MessageBoxButtons.OK, MessageBoxIcon.Error);		
		}
	}

	private void BinScanned(object sender, EventArgs args)
	{
		if(!chkAutoQty.Checked)
		{
			qty++;
			_edvShipDtl.CurrentDataRow["DisplayInvQty"] = decimal.Parse(_edvShipDtl.CurrentDataRow["DisplayInvQty"].ToString()) + 1;
		}
		int i = 0;
		foreach (DataRow row in this._salesOrderAdapter.SalesOrderData.OrderRel.Rows)
		{
			if(row["OrderNum"].ToString() == _edvShipDtl.CurrentDataRow["OrderNum"].ToString() && 
			   row["OrderLine"].ToString() == _edvShipDtl.CurrentDataRow["OrderLine"].ToString() && 
			   row["OrderRelNum"].ToString() == _edvShipDtl.CurrentDataRow["OrderRelNum"].ToString())
			{
				grdQuantity.Rows[i].Cells["Remain Qty"].Value = decimal.Parse(row["SellingReqQty"].ToString()) - qty - decimal.Parse(row["TotalJobStockShipped"].ToString());
				grdQuantity.Rows[i].Cells["Pack Qty"].Value = qty + decimal.Parse(row["TotalJobStockShipped"].ToString());
				if((int)grdQuantity.Rows[i].Cells["Remain Qty"].Value == 0)
				{
					_edvShipDtl.CurrentDataRow["BinNum"] = txtBinNum.Text;
					_custShipAdapter.Update();
					txtOrder.Focus();
					qty = 0;
					NewLine();
				}
				else
				{
					if(!chkAutoQty.Checked)
					{
						_edvShipDtl.CurrentDataRow["PartNum"] = "";
						txtPartNum.Text = "";
						txtPartNum.Focus();
					}
				}
				break;
			}
			i++;
		}
	}

	private void NewLine()
	{
		_custShipAdapter.GetNewShipDtl((int)this._edvShipHead.dataView[0]["PackNum"]);
		this._edvShipDtl.Row = this._edvShipDtl.dataView.Count - 1;
		this._edvShipDtl.Notify(new EpiNotifyArgs(this.oTrans, this._edvShipDtl.Row, this._edvShipDtl.Column));
	}

	private void NewRelease(object sender, EventArgs args)
	{
		_custShipAdapter.Update();
		txtOrder.Focus();
		qty = 0;
		NewLine();
	}

	private void CloseShipment(object sender, EventArgs args)
	{
		DataRow deleteRow = null;
		foreach (DataRow row in this._custShipAdapter.CustShipData.ShipDtl.Rows)
		{
			if(row["OrderNum"].ToString() == "0" || 
			   row["OrderLine"].ToString() == "0" || 
			   row["OrderRelNum"].ToString() == "0")
			{
				deleteRow = row;
				break;
			}
		}
		if(deleteRow != null)
		{
			_custShipAdapter.Delete(deleteRow);
		}
		_custShipAdapter.Update();
		this._edvShipHead.dataView[0]["ReadyToInvoice"] = true;
		CallCustShipAdapterUpdateMasterMethod();
		foreach (DataRow row in this._custShipAdapter.CustShipData.ShipDtl.Rows)
		{
			if((bool)row["ShipCmpl"])
			{
				CallSalesOrderAdapterCloseReleaseMethod((int)row["OrderNum"], (int)row["OrderLine"], (int)row["OrderRelNum"]);
			}
		}
		this._custShipAdapter.CustShipData.Clear();
		this._salesOrderAdapter.SalesOrderData.Clear();
		this._edvShipDtl.Row = this._edvShipDtl.dataView.Count - 1;
		this._edvShipDtl.Notify(new EpiNotifyArgs(this.oTrans, this._edvShipDtl.Row, this._edvShipDtl.Column));
		this._edvShipHead.Notify(new EpiNotifyArgs(this.oTrans, this._edvShipHead.Row, this._edvShipHead.Column));
		txtLoadID.Focus();
		txtLoadID.Text = "";
		this._LoadIDSalesOrder = "";
		qty = 0;
	}

	private void ShipDtl_BeforeFieldChange(object sender, DataColumnChangeEventArgs args)
	{
		// ** Argument Properties and Uses **
		// args.Row["FieldName"]
		// args.Column, args.ProposedValue, args.Row
		// Add Event Handler Code
		switch (args.Column.ColumnName)
		{
			case "BinNum":
				if(_edvShipDtl.CurrentDataRow["BinNum"].ToString() != txtBinNum.Text && _edvShipDtl.CurrentDataRow["BinNum"].ToString() != "")
				{
					string bin = txtBinNum.Text;
					args.ProposedValue = _edvShipDtl.CurrentDataRow["BinNum"].ToString();
					_custShipAdapter.Update();
					NewLine();
					_edvShipDtl.CurrentDataRow["OrderNum"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["OrderNum"];
					_edvShipDtl.CurrentDataRow["OrderLine"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["OrderLine"];
					_edvShipDtl.CurrentDataRow["OrderRelNum"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["OrderRelNum"];
					_edvShipDtl.CurrentDataRow["CustNum"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["CustNum"];
					_edvShipDtl.CurrentDataRow["WarehouseCode"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["WarehouseCode"];
					_edvShipDtl.CurrentDataRow["PartNum"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["PartNum"];
					_edvShipDtl.CurrentDataRow["LineDesc"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["LineDesc"];
					_edvShipDtl.CurrentDataRow["InventoryShipUOM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["InventoryShipUOM"];
					_edvShipDtl.CurrentDataRow["IUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["IUM"];
					_edvShipDtl.CurrentDataRow["SalesUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["SalesUM"];
					_edvShipDtl.CurrentDataRow["JobShipUOM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["JobShipUOM"];
					_edvShipDtl.CurrentDataRow["OurRemainUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["OurRemainUM"];
					_edvShipDtl.CurrentDataRow["OurReqUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["OurReqUM"];
					_edvShipDtl.CurrentDataRow["OurShippedUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["OurShippedUM"];
					_edvShipDtl.CurrentDataRow["SellingRemainUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["SellingRemainUM"];
					_edvShipDtl.CurrentDataRow["SellingReqUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["SellingReqUM"];
					_edvShipDtl.CurrentDataRow["SellingShipmentUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["SellingShipmentUM"];
					_edvShipDtl.CurrentDataRow["SellingShippedUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["SellingShippedUM"];
					_edvShipDtl.CurrentDataRow["PartNumSalesUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["PartNumSalesUM"];
					_edvShipDtl.CurrentDataRow["PartNumIUM"] = _edvShipDtl.dataView[_edvShipDtl.Row - 1]["PartNumIUM"];
					_edvShipDtl.CurrentDataRow["BinNum"] = bin;
				}
				break;
		}
	}

	private void CallCustShipAdapterUpdateMasterMethod()
	{
		try
		{
			// Declare and Initialize Variables
			bool doValidateCreditHold = true;
			bool doCheckShipDtl = true;
			bool doLotValidation = false;
			bool doCheckOrderComplete = true;
			bool doPostUpdate = true;
			bool doCheckCompliance = false;
			bool ipShippedFlagChanged = true;
			int ipPackNum = (int)this._edvShipHead.dataView[0]["PackNum"];
			int ipBTCustNum = (int)this._edvShipHead.dataView[0]["BTCustNum"];
			System.String opReleaseMessage;
			System.String opCompleteMessage;
			System.String opShippingMessage;
			System.String opLotMessage;
			System.String opInventoryMessage;
			System.String opLockQtyMessage;
			System.String opAllocationMessage;
			System.String opPartListNeedsAttr;
			System.String opLotListNeedsAttr;
			System.String shipCreditMsg;
			System.Boolean cError;
			System.Boolean compError;
			System.String msg;
			System.String opPostUpdMessage;
			System.Boolean updateComplete;
			System.Boolean checkComplianceError;
			System.Boolean changeStatusError;

			// Call Adapter method
			bool result = _custShipAdapter.UpdateMaster(doValidateCreditHold, doCheckShipDtl, doLotValidation, doCheckOrderComplete, doPostUpdate, doCheckCompliance, ipShippedFlagChanged, ipPackNum, ipBTCustNum, out opReleaseMessage, out opCompleteMessage, out opShippingMessage, out opLotMessage, out opInventoryMessage, out opLockQtyMessage, out opAllocationMessage, out opPartListNeedsAttr, out opLotListNeedsAttr, out shipCreditMsg, out cError, out compError, out msg, out opPostUpdMessage, out updateComplete, out checkComplianceError, out changeStatusError);

		} catch (System.Exception ex)
		{
			MessageBox.Show(ex.ToString());
		}
	}

	private void CallSalesOrderAdapterCloseReleaseMethod(int iOrderNum, int iOrderLine, int iOrderRelNum)
	{
		try
		{
			// Call Adapter method
			bool result = _salesOrderAdapter.CloseRelease(iOrderNum, iOrderLine, iOrderRelNum);

		} catch (System.Exception ex)
		{
			MessageBox.Show(ex.ToString());
		}
	}
}
